import axios from 'axios';
import React, { Component } from 'react'
const responseURL="http://localhost:8080";
const UserContext = React.createContext();
const reducer=async(state,action)=>{
  switch(action.type){
    case 'showCompany':
     { 
      console.log('çalışıyor '+action.payload);
      var asd= state.sirketTableData.rows.filter(row=>{return action.payload===row.company_name}).company_name;
      console.log(asd);
      return{...state,
        sirketTableData: {rows:state.sirketTableData.rows.filter(sirket=>sirket.company_name===action.payload)}
      }}
    case 'studentIdGet':{
      try{
        state.ogrenci.id= action.payload;
        return{...state,state}
      }
      catch(e){
        return console.log('context hata: '+e);
      }
      
    }
    case 'showIntership':{
      try{
        const ogrenciID=action.payload;
        const response=await axios.get(responseURL+"/getStudentApplication/"+ogrenciID);
        
        const jsonstring= JSON.stringify(response.data);
        const data = JSON.parse(jsonstring);action.asd=data;
        state.stajlar=action.asd;
        return {...state,stajlar:action.asd};
      }catch(e){
        return console.log("hata :"+e);
      }
    }
    default:
      return state;
  }
}

export  class Context extends Component {
  
   state={
      sirketTableData : {
        columns: ['company_name','sector','email'],
        showColumns:['Şirket','Sektör','E-mail'],
        rows:[ ]
      },
      stajlar:[]
      ,
      ogrenci:{
        id:''
      },
      mountYonlendirici:''
      ,
      dispatch:async  action=> {
        this.setState(state=>reducer(state,action))
      },
   }  
  
  componentDidMount= async () =>{
    const response=await  axios.get(responseURL+"/showCompany");
    const jsonstring= JSON.stringify(response.data);
    const data = JSON.parse(jsonstring);
    this.setState({
      
      sirketTableData : {
        columns: ['company_name','sector','email'],
        showColumns:['Şirket','Sektör','E-mail'],
        rows:data,
      }
    })
    
  }
  
  render() {
    
    return (
      <UserContext.Provider value={this.state}>
        {this.props.children}
      </UserContext.Provider>
    )
  }
}

const UserConsumer = UserContext.Consumer;

export default UserConsumer;
