import React, { Component } from 'react';
import uuid from 'react-uuid';
import UserConsumer from '../Context';
import YeniStajModal from './YeniStajModal';
export default class StajBilgiEkrani extends Component {
  constructor(props){
    super(props);
    this.state={
      isVisible: false,
      butonGecerliMi: true,
      deger:{}
    }
    this.onGetir = this.onGetir.bind(this);
  }
  
  onClickEvent=(e)=>{
    this.setState({
      isVisible: !this.state.isVisible,
    })
  }
  onButonEvent=(e)=>{
    this.setState({
      butonGecerliMi: !this.state.butonGecerliMi,
    })
  }
  onGetir=(dispatch,ogrenciID,e)=>{
    try{
      console.log(ogrenciID+" staj bilgi ekrani");
      return dispatch({type:"showIntership",ogrenciID});
    }
    catch(e){
      return console.log("hata var")
    }
    
  }
  componentDidMount(){
    <UserConsumer>
      {value=>{
        this.onGetir(value.dispatch,value.ogrenciID)
      }}
    </UserConsumer>
    
  }
  render() {
    
    var deger={}
    return(
      
      <UserConsumer>
      {
        value=>{
            deger=value;
             console.log(deger);
            
            const state= this.state;
            const {isVisible}= this.state;
            const stajDonemi="2021-2022 dÃ¶nemi";
            const stajBilgileri = ['web sitesi','20.06.2022','08.08.2022','30',true,'30']
             
            //var t= stajlar.filter(a=>a.id ===ogrenci.id);
            function stajlariGoster(){
              const staj=[];
              //console.log(t);
              console.log(value.stajlar);
              staj.push(stajCard)
              return staj;
            }
            // var cardBody=stajlar.map(function(row){
            
            //   <div className='card-body '>
            //       <div className='row '>

            //       </div>
            //   </div>
            // })
            var stajCard=value.stajlar.map(function(staj){
              return(
                <div className="card " style={{  }} key={uuid()} >
                <div className="card-header" ><h4 onClick={this.onClickEvent}>{stajDonemi}</h4></div>
                {
                    isVisible ? 
                    <div className='card-body '>
                        <div className='row '>
                          
                          <div className='col'>StajÄ±n Konusu: {staj.subject}</div>
                          <div className='col'>StajÄ±n BaÅlangÄ±Ã§ Tarihi: {stajBilgileri[1]}</div>
                          <div className='col'>StajÄ±n BitiÅ Tarihi:{stajBilgileri[2]}</div>
                        </div>
                        <div className='row'>
                          <div className='col'>StajÄ±n GÃ¼n SayÄ±sÄ±:{stajBilgileri[3]}</div>
                          <div className='col'>Komisyon OnayÄ±:
                            
                            <input
                              className="form-check-input"
                              type="checkbox"
                              defaultValue=""
                              id="flexCheckDisabled"
                              defaultChecked={stajBilgileri[4]}
                              disabled
                            />
                              
                          </div>
                          <div className='col'>Kabul Edilen GÃ¼n SayÄ±sÄ±:{stajBilgileri[5]}</div>
                        </div>
                    </div>
                    : null 
                }
                </div>
              );
              

            }
            );
              
            
            return (
                <div className='col'>
                <h4 >Staj Listesi</h4>
                <div >
                   
                    {stajlariGoster() }
                    
                </div>
                    <br/>
                    <br/>
                    <br/>
                <div className=''>
                    { state.butonGecerliMi ? <YeniStajModal/> : null}
                    
                </div>
                
              </div>
            )
          
        }
      }
    </UserConsumer>
    )
    
    
  }
}
