import axios from 'axios';
import React, { Component } from 'react';
import uuid from 'react-uuid';
import UserConsumer from '../../Context';
import StajCard from './StajCard';
import YeniStajModal from './YeniStajModal';

const responseURL="http://localhost:8080";
export default class StajBilgiEkrani extends Component {
  constructor(props){
    super(props);
    this.state={
      isVisible: false,
      butonGecerliMi: true,
      stajlar:[],
      ogrenci:{},
      dispatch:{},
    }
    this.onStajlar =this.onStajlar.bind(this);
  }
 
  onButonEvent=(e)=>{
    this.setState({
      butonGecerliMi: !this.state.butonGecerliMi,
    })
  }
  
   onStajlar= async(ogrenciID)=>{
    //console.log("gelmiyor");
    try{
      //e.preventDefault();
      //console.log("çalışıyor");
      //dispatch({type:'showIntership',payload:ogrenciID});
      const response=await axios.get(responseURL+"/getStudentApplication/"+ogrenciID);
      const jsonstring= JSON.stringify(response.data);
      const data = JSON.parse(jsonstring);
      
      this.setState({
          stajlar:data
      })
      return <UserConsumer>{value=>value.stajlar=data}</UserConsumer>
    }catch(e){
      return console.log("hata :"+e);
    }
  }
  componentDidMount = async(e)=>{
    try {
      const {dispatch}=this.state;
      const ogrenciID=this.state.ogrenci.id;
      await this.onStajlar(ogrenciID);
      //dispatch({type:'showIntership',payload:ogrenciID});
      //console.log(this.state.stajlar);
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    try {
      var state= this.state;
      return(
        <UserConsumer>
          {
            value=>{
                
                state.ogrenci=value.ogrenci;
                state.dispatch=value.dispatch;
                value.mountYonlendirici= 'StajlariGetir';
                var stajlariGoster=()=>{
                  const staj=[];
                  staj.push(stajCard)
                  return staj;
                }
                var {stajlar}=this.state;    
                var stajCard=stajlar.map(function(staj){
                    
                    return(
                      <StajCard staj={staj} key={uuid()}></StajCard>
                    );
                  });
                  
                
                  
              return (
                    <div className='col'>
                    <h4 >Staj Listesi</h4>
                    <div >
                        {stajlariGoster() }
                    </div>
                        <br/>
                        <br/>
                        <br/>
                    <div className=''>
                        { state.butonGecerliMi ? <YeniStajModal/> : null}
                        
                    </div>
                    
                  </div>
              );
              
            }
          }
        </UserConsumer>
      )
    
    } catch (error) {
      return(console.log(error))
      
    }
    
    
  }
}
