import React, { Component } from 'react';
import uuid from 'react-uuid';
import UserConsumer from '../Context';
import YeniStajModal from './YeniStajModal';
export default class StajBilgiEkrani extends Component {
  constructor(props){
    super(props);
    this.state={
      isVisible: false,
      butonGecerliMi: true,
      deger:{}
    }
    this.onGetir = this.onGetir.bind(this);
  }
  
  onClickEvent=(e)=>{
    this.setState({
      isVisible: !this.state.isVisible,
    })
  }
  onButonEvent=(e)=>{
    this.setState({
      butonGecerliMi: !this.state.butonGecerliMi,
    })
  }
  onGetir=(dispatch,ogrenciID,e)=>{
    try{
      this.setState({
        deger:dispatch({type:"showIntership",ogrenciID})
      })
      return (
        console.log(this.state.deger)
      );
    }
    catch(e){
      return console.log("hata var")
    }
    
  }
  
  componentDidMount(){
    try {
      
        <UserConsumer>
          {value=>{
            this.setState({deger:value})
          }}
        </UserConsumer>
      
      this.onGetir(this.state.deger.dispatch,this.state.deger.ogrenci.id)
    } catch (error) {
      console.log(error);
    }
    
    
  }
  render() {
    var state= this.state;
    return(
      <UserConsumer>
      {
        value=>{
            state.deger=value;
            
            const {isVisible}= this.state;
            const stajDonemi="2021-2022 dönemi";
            const stajBilgileri = ['web sitesi','20.06.2022','08.08.2022','30',true,'30']
            console.log(state.stajlar+" render içi");
            //var t= stajlar.filter(a=>a.id ===ogrenci.id);
            function stajlariGoster(){
              const staj=[];
              
              console.log(state.deger.stajlar+" staj 70");
              staj.push(stajCard)
              return staj;
            }
            // var cardBody=stajlar.map(function(row){
            
            //   <div className='card-body '>
            //       <div className='row '>

            //       </div>
            //   </div>
            // })
            var stajCard=state.deger.stajlar.map(function(staj){
              return(
                <div className="card " style={{  }} key={uuid()} >
                <div className="card-header" ><h4 onClick={this.onClickEvent}>{stajDonemi}</h4></div>
                {
                    isVisible ? 
                    <div className='card-body '>
                        <div className='row '>
                          
                          <div className='col'>Stajın Konusu: {staj.subject}</div>
                          <div className='col'>Stajın Başlangıç Tarihi: {stajBilgileri[1]}</div>
                          <div className='col'>Stajın Bitiş Tarihi:{stajBilgileri[2]}</div>
                        </div>
                        <div className='row'>
                          <div className='col'>Stajın Gün Sayısı:{stajBilgileri[3]}</div>
                          <div className='col'>Komisyon Onayı:
                            
                            <input
                              className="form-check-input"
                              type="checkbox"
                              defaultValue=""
                              id="flexCheckDisabled"
                              defaultChecked={stajBilgileri[4]}
                              disabled
                            />
                              
                          </div>
                          <div className='col'>Kabul Edilen Gün Sayısı:{stajBilgileri[5]}</div>
                        </div>
                    </div>
                    : null 
                }
                </div>
              );
              

            }
            );
              
            
            return (
                <div className='col'>
                <h4 >Staj Listesi</h4>
                <div >
                   
                    {stajlariGoster() }
                    
                </div>
                    <br/>
                    <br/>
                    <br/>
                <div className=''>
                    { state.butonGecerliMi ? <YeniStajModal/> : null}
                    
                </div>
                
              </div>
            )
          
        }
      }
    </UserConsumer>
    )
    
    
  }
}
