import axios from 'axios';
import React, { Component } from 'react';
import uuid from 'react-uuid';
import UserConsumer from '../../Context';
import YeniStajModal from '../StajComponents/YeniStajModal';

const responseURL="http://localhost:8080";
export default class StajBilgiEkrani extends Component {
  constructor(props){
    super(props);
    this.state={
      isVisible: false,
      butonGecerliMi: true,
      stajlar:[],
      ogrenci:{}
    }
    
  }
  onClickEvent=(e)=>{
    
    this.setState({
      isVisible: !this.state.isVisible,
    })
  }
  onButonEvent=(e)=>{
    
    this.setState({
      butonGecerliMi: !this.state.butonGecerliMi,
    })
  }
  async onStajlar(ogrenciID){
    try{
      const response=await axios.get(responseURL+"/getStudentApplication/"+ogrenciID);
      const jsonstring= JSON.stringify(response.data);
      const data = JSON.parse(jsonstring);
      this.setState({
          stajlar:data
      })
    }catch(e){
      return console.log("hata :"+e);
    }
  }
  componentDidMount= (e)=>{
    try {
      this.onStajlar(this.state.ogrenci.id)
      
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    try {
      var state= this.state;
      return(
        <UserConsumer>
          {
            value=>{
                state.ogrenci=value.ogrenci;
                // ogrenci numarası ornek:3091113113    ,    1903323122
                
                var eventt= this.onClickEvent;
                const stajDonemi="2021-2022 dönemi";
                const stajBilgileri = ['web sitesi','20.06.2022','08.08.2022','30',true,'30']
                //var t= stajlar.filter(a=>a.id ===ogrenci.id);
                var stajlariGoster=()=>{
                  const staj=[];
                  staj.push(stajCard)
                  return staj;
                }
                var {stajlar}=this.state;    
              var stajCard=stajlar.map(function(staj){
                    const {isVisible}= state;
                      return(
                        <div className="card " style={{  }} key={uuid()} >
                        <div className="card-header" ><h4 onClick={eventt}>{stajDonemi}</h4></div>
                        {
                            isVisible ? 
                            <div className='card-body '>
                                <div className='row '>
                                  
                                  <div className='col'>Stajın Konusu: {staj.subject}</div>
                                  <div className='col'>Stajın Başlangıç Tarihi: {stajBilgileri[1]}</div>
                                  <div className='col'>Stajın Bitiş Tarihi:{stajBilgileri[2]}</div>
                                </div>
                                <div className='row'>
                                  <div className='col'>Stajın Gün Sayısı:{stajBilgileri[3]}</div>
                                  <div className='col'>Komisyon Onayı:
                                    
                                    <input
                                      className="form-check-input"
                                      type="checkbox"
                                      defaultValue=""
                                      id="flexCheckDisabled"
                                      defaultChecked={stajBilgileri[4]}
                                      disabled
                                    />
                                      
                                  </div>
                                  <div className='col'>Kabul Edilen Gün Sayısı:{stajBilgileri[5]}</div>
                                </div>
                            </div>
                            : null 
                        }
                        </div>
                      );
              });
                  
                
                  
              return (
                    <div className='col'>
                    <h4 >Staj Listesi</h4>
                    <div >
                      
                        {stajlariGoster() }
                        
                    </div>
                        <br/>
                        <br/>
                        <br/>
                    <div className=''>
                        { state.butonGecerliMi ? <YeniStajModal/> : null}
                        
                    </div>
                    
                  </div>
              );
              
            }
          }
        </UserConsumer>
      )
    
    } catch (error) {
      return(console.log(error))
      
    }
    
    
  }
}
