import axios from 'axios';
import React, { Component } from 'react';
import uuid from 'react-uuid';
import UserConsumer from '../../Context';
import StajCard from './StajCard';
import YeniStajModal from './YeniStajModal';

const responseURL="http://localhost:8080";
export default class StajBilgiEkrani extends Component {
  constructor(props){
    super(props);
    this.state={
      isVisible: false,
      butonGecerliMi: true,
      stajlar:[],
      ogrenci:{},
      dispatch:{},
    }
  }
 
  onButonEvent=(e)=>{
    
    this.setState({
      butonGecerliMi: !this.state.butonGecerliMi,
    })
  }
   onStajlar= async(ogrenciID,dispatch,e)=>{
    try{
      e.preventDefault();
      dispatch({type:'showIntership',payload:ogrenciID});
      const response=await axios.get(responseURL+"/getStudentApplication/"+ogrenciID);
      const jsonstring= JSON.stringify(response.data);
      const data = JSON.parse(jsonstring);
      this.setState({
          stajlar:data
      })
    }catch(e){
      return console.log("hata :"+e);
    }
  }
  componentDidMount= (e)=>{
    try {
      this.onStajlar(this.state.ogrenci.id,this.state.dispatch,e)
      
    } catch (error) {
      console.log(error);
    }
  }
  render() {
    try {
      var state= this.state;
      return(
        <UserConsumer>
          {
            value=>{
                state.ogrenci=value.ogrenci;
                state.dispatch=value.dispatch;
                var stajlariGoster=()=>{
                  const staj=[];
                  staj.push(stajCard)
                  return staj;
                }
                var {stajlar}=this.state;    
                var stajCard=stajlar.map(function(staj){
                    
                    return(
                      <StajCard staj={staj} key={uuid()}></StajCard>
                    );
                  });
                  
                
                  
              return (
                    <div className='col'>
                    <h4 >Staj Listesi</h4>
                    <div >
                      
                        {stajlariGoster() }
                        
                    </div>
                        <br/>
                        <br/>
                        <br/>
                    <div className=''>
                        { state.butonGecerliMi ? <YeniStajModal/> : null}
                        
                    </div>
                    
                  </div>
              );
              
            }
          }
        </UserConsumer>
      )
    
    } catch (error) {
      return(console.log(error))
      
    }
    
    
  }
}
